mapper.select:
SELECT DISTINCT ID, TEXT, UPPERID, ORDINAL, REPORT_TYPE, TYPE, REG_DT FROM ((SELECT DISTINCT PUB_FLD_MSTR.FLD_ID AS ID, PUB_FLD_MSTR.FLD_NM AS TEXT, PUB_FLD_MSTR.FLD_PARENT_ID AS UPPERID, PUB_FLD_MSTR.FLD_ORDINAL AS ORDINAL, NULL AS REPORT_TYPE, 'FOLDER' AS TYPE, NULL AS REG_DT FROM USER_AUTH_REPORT_MSTR INNER JOIN USER_MSTR ON USER_MSTR.USER_NO = USER_AUTH_REPORT_MSTR.USER_NO INNER JOIN PUB_FLD_MSTR ON PUB_FLD_MSTR.FLD_ID = USER_AUTH_REPORT_MSTR.FLD_ID
WHERE USER_MSTR.USER_ID = #{userId} AND AUTH_PUBLISH != 'N'
WHERE USER_MSTR.USER_ID = #{userId} AND AUTH_VIEW != 'N'
) UNION ALL (SELECT DISTINCT PUB_FLD_MSTR.FLD_ID AS ID, PUB_FLD_MSTR.FLD_NM AS TEXT, PUB_FLD_MSTR.FLD_PARENT_ID AS UPPERID, PUB_FLD_MSTR.FLD_ORDINAL AS ORDINAL, NULL AS REPORT_TYPE, 'FOLDER' AS TYPE, NULL AS REG_DT FROM GRP_AUTH_REPORT_MSTR INNER JOIN USER_MSTR ON USER_MSTR.GRP_ID = GRP_AUTH_REPORT_MSTR.GRP_ID INNER JOIN PUB_FLD_MSTR ON PUB_FLD_MSTR.FLD_ID = GRP_AUTH_REPORT_MSTR.FLD_ID
WHERE USER_MSTR.USER_ID = #{userId} AND AUTH_PUBLISH != 'N'
WHERE USER_MSTR.USER_ID = #{userId} AND AUTH_VIEW != 'N'
) UNION ALL (SELECT REPORT_MSTR.REPORT_ID AS ID, REPORT_MSTR.REPORT_NM AS TEXT, REPORT_MSTR.FLD_ID AS UPPERID, REPORT_MSTR.REPORT_ORDINAL AS ORDINAL, REPORT_MSTR.REPORT_TYPE AS REPORT_TYPE, 'REPORT' AS TYPE, REPORT_MSTR.REG_DT AS REG_DT FROM REPORT_MSTR INNER JOIN PUB_FLD_MSTR ON REPORT_MSTR.FLD_ID = PUB_FLD_MSTR.FLD_ID WHERE REPORT_MSTR.DEL_YN != 'Y'
AND REPORT_MSTR.REPORT_TYPE IN
#{type}
) ) A ORDER BY UPPERID, TYPE, ORDINAL

SELECT * FROM (( SELECT DISTINCT USER_FLD_MSTR.FLD_ID AS ID, USER_FLD_MSTR.FLD_NM AS TEXT, USER_FLD_MSTR.FLD_PARENT_ID AS UPPERID, USER_FLD_MSTR.FLD_ORDINAL AS ORDINAL, NULL AS REPORT_TYPE, 'FOLDER' AS TYPE, NULL AS REG_DT FROM USER_FLD_MSTR INNER JOIN USER_MSTR ON USER_MSTR.USER_NO = USER_FLD_MSTR.USER_NO WHERE USER_MSTR.USER_ID = #{userId} ) UNION ALL (SELECT REPORT_MSTR.REPORT_ID AS ID, REPORT_MSTR.REPORT_NM AS TEXT, REPORT_MSTR.FLD_ID AS UPPERID, REPORT_MSTR.REPORT_ORDINAL AS ORDINAL, REPORT_MSTR.REPORT_TYPE AS REPORT_TYPE, 'REPORT' AS TYPE, REPORT_MSTR.REG_DT AS REG_DT FROM REPORT_MSTR INNER JOIN USER_FLD_MSTR ON REPORT_MSTR.FLD_ID = USER_FLD_MSTR.FLD_ID WHERE REPORT_MSTR.DEL_YN != 'Y'
AND REPORT_MSTR.REPORT_TYPE IN
#{type}
) ) A ORDER BY UPPERID, TYPE, ORDINAL

mapper.insert:
SELECT MAX(REPORT_ID) FROM REPORT_MSTR
SELECT REPORT_ID as reportId FROM REPORT_MSTR WHERE REPORT_ID = #{reportId}
MERGE INTO REPORT_MSTR USING dual ON (#{reportId} != 0) WHEN MATCHED THEN UPDATE SET REPORT_NM = #{reportNm}, REPORT_SUB_TITLE = #{reportSubTitle}, FLD_ID = #{fldId}, FLD_TYPE = #{fldType}, REPORT_ORDINAL = #{reportOrdinal}, REPORT_TYPE = #{reportType}, REPORT_DESC = #{reportDesc}, REPORT_XML = #{reportXml}, CHART_XML = #{chartXml}, LAYOUT_XML = #{layoutXml}, PARAM_XML = #{paramXml}, REG_USER_NO = #{regUserNo} WHERE REPORT_ID = #{reportId} WHEN NOT MATCHED THEN INSERT ( REPORT_ID, REPORT_NM, REPORT_SUB_TITLE, FLD_ID, FLD_TYPE, REPORT_ORDINAL, REPORT_TYPE, REPORT_DESC, REPORT_XML, CHART_XML, LAYOUT_XML, DATASET_XML, PARAM_XML, REG_USER_NO ) VALUES ( REPORT_MSTR_REPORT_ID_SEQ.NEXTVAL, #{reportNm}, #{reportSubTitle}, #{fldId}, #{fldType}, #{reportOrdinal}, #{reportType}, #{reportDesc}, #{reportXml}, #{chartXml}, #{layoutXml}, #{datasetXml}, #{paramXml}, #{regUserNo} )

mapper.update:
UPDATE REPORT_MSTR
SET DEL_YN = 'Y'
WHERE REPORT_ID = #{reportId}
